import React, { useState } from 'react'; 
import { addBike } from '../services/BikeService'; 
import '../styles/addBike.css';

const AddBike = ({ onAddBike }) => { 
  const [manufacturer, setManufacturer] = useState(''); 
  const [model, setModel] = useState(''); 
  const [price, setPrice] = useState('');

  const handleSubmit = async (e) => { 
    e.preventDefault(); 
    
    if (manufacturer && model && price) { 
      // Fiyatı '€' simgesiyle formatlıyoruz
      const formattedPrice = `€${parseFloat(price).toFixed(2)}`;
      
      // Yeni bisiklet objesini oluşturuyoruz
      const newBike = { 
        manufacturer, 
        model, 
        price: formattedPrice,  // Formatlanmış fiyatı gönderiyoruz
        category: 'Mountain Bike', // Varsayılan kategori
        img_url: '/assets/images/default-bike.png', // Varsayılan resim
      };

      // Backend'e yeni bisiklet verisini gönderiyoruz
      const result = await addBike(newBike); 
      onAddBike(result); 
      
      // Formu sıfırlıyoruz
      setManufacturer(''); 
      setModel(''); 
      setPrice('');
    } 
  };

  return ( 
    <div className="add-bike-container">
      <h2>Add a New Bike</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          className="input-field"
          placeholder="Manufacturer"
          value={manufacturer}
          onChange={(e) => setManufacturer(e.target.value)}
          required
        />
        <input
          type="text"
          className="input-field"
          placeholder="Model"
          value={model}
          onChange={(e) => setModel(e.target.value)}
          required
        />
        <input
          type="number"
          className="input-field"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />
        <button type="submit" className="submit-btn">Add Bike</button>
      </form>
    </div>
  );
};

export default AddBike;
