// BikeList.js
import React from 'react';
import '../styles/bikeList.css';

const BikeList = ({ bikes }) => {
  return (
    <div className="bike-list">
      {bikes.map((bike, index) => (
        <div key={index} className="bike-item">
          <div className="bike-image">
            {/* Use relative path from the public folder */}
            <img 
              src={`/assets/images/bikes/${bike.img_url}`} 
              alt={bike.model} 
            />
          </div>
          <div className="bike-info">
            <h3 className="bike-manufacturer">{bike.manufacturer}</h3>
            <p className="bike-model">{bike.model}</p>
            <p className="bike-price">{bike.price}</p>
          </div>
        </div>
      ))}
    </div>
  );
};


export default BikeList;
