// src/components/BikeList.js
import React, { useEffect, useState } from 'react';
import { getBikes, addBike } from '../services/api';
import BikeCard from './BikeCard';

const BikeList = () => {
  const [bikes, setBikes] = useState([]);
  const [newBike, setNewBike] = useState({ manufacturer: '', model: '', price: '' });

  useEffect(() => {
    // Fetch bikes data when the component mounts
    const fetchBikes = async () => {
      const fetchedBikes = await getBikes();
      setBikes(fetchedBikes);
    };
    fetchBikes();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewBike((prevBike) => ({
      ...prevBike,
      [name]: value,
    }));
  };

  const handleAddBike = async (e) => {
    e.preventDefault();
    if (newBike.manufacturer && newBike.model && newBike.price) {
      const addedBike = await addBike(newBike);
      setBikes((prevBikes) => [...prevBikes, addedBike]);
      setNewBike({ manufacturer: '', model: '', price: '' });
    } else {
      alert('All fields are required.');
    }
  };

  return (
    <div className="bike-list-container">
      <h2>Bike Catalogue</h2>
      <div className="bike-list">
        {bikes.length > 0 ? (
          bikes.map((bike) => <BikeCard key={bike.id} bike={bike} />)
        ) : (
          <p>No bikes available.</p>
        )}
      </div>
      <form onSubmit={handleAddBike} className="add-bike-form">
        <h3>Add a New Bike</h3>
        <input
          type="text"
          name="manufacturer"
          placeholder="Manufacturer"
          value={newBike.manufacturer}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="model"
          placeholder="Model"
          value={newBike.model}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={newBike.price}
          onChange={handleInputChange}
          required
        />
        <button type="submit">Add Bike</button>
      </form>
    </div>
  );
};

export default BikeList;
