// src/App.js
import React, { useState, useEffect } from 'react';
import BikeList from './components/BikeList';
import Dropdown from './components/Dropdown';
import { getBikes } from './services/BikeService';
import './styles/app.css';

const App = () => {
  const [bikes, setBikes] = useState([]);
  const [sortBy, setSortBy] = useState('manufacturer');

  // Bisiklet verilerini API'den çekme
  useEffect(() => {
    const fetchBikes = async () => {
      const bikeData = await getBikes();
      setBikes(bikeData); // Bisikletleri state'e yükle
    };
    fetchBikes();
  }, []);

  // Sıralama tercihini güncelleme
  const handleSortChange = (value) => {
    setSortBy(value);
  };

  // Yeni bir bisiklet ekleme
  const handleAddBike = (newBike) => {
    setBikes((prevBikes) => [...prevBikes, newBike]); // Yeni bisikleti ekle
  };

  // Bisikletleri sıralama
  const sortedBikes = [...bikes].sort((a, b) => {
    if (sortBy === 'price') {
      return a.price - b.price; // Fiyat sırasına göre
    } else if (sortBy === 'manufacturer') {
      return a.manufacturer.localeCompare(b.manufacturer); // Üreticiye göre
    }
    return 0;
  });

  return (
    <div className="app-container">
      <h1 className="app-title">Bike Catalogue</h1>
      
      {/* Dropdown bileşenini ekle */}
      <Dropdown onSortChange={handleSortChange} />
      
      {/* Yeni bisiklet ekleme formu */}
      <div className="add-bike-container">
        <h3>Add New Bike</h3>
        {/* AddBike bileşenini sadece burada ekliyoruz */}
        <AddBike onAddBike={handleAddBike} />
      </div>
      
      {/* Bisikletlerin listelendiği alan */}
      <div className="bike-list-container">
        <BikeList bikes={sortedBikes} /> {/* Sıralanmış bisikletleri listeliyoruz */}
      </div>
    </div>
  );
};

export default App;
