import React, { useEffect, useState } from 'react';
import { getBikes } from '../services/api';
import BikeCard from './BikeCard';
import NewBikeForm from './NewBikeForm';

const BikeList = () => {
  const [bikes, setBikes] = useState([]);

  useEffect(() => {
    const fetchBikes = async () => {
      const fetchedBikes = await getBikes();
      setBikes(fetchedBikes);
    };
    fetchBikes();
  }, []);

  const handleBikeAdded = (addedBike) => {
    setBikes((prevBikes) => [...prevBikes, addedBike]);
  };

  return (
    <div className="bike-list-container">
      <h2>Bike Catalogue</h2>
      <div className="bike-list">
        {bikes.length > 0 ? (
          bikes.map((bike) => <BikeCard key={bike.id} bike={bike} />)
        ) : (
          <p>No bikes available.</p>
        )}
      </div>
      <NewBikeForm onBikeAdded={handleBikeAdded} />
    </div>
  );
};

export default BikeList;
