import React, { useEffect, useState } from 'react';
import { getBikes } from '../services/BikeService';
import BikeCard from './BikeCard';  // Her bisikleti gösterecek bileşen

const BikeList = () => {
  const [bikes, setBikes] = useState([]);

  useEffect(() => {
    const fetchBikes = async () => {
      const data = await getBikes();
      setBikes(data);  // JSON verisini state'e aktar
    };

    fetchBikes();
  }, []);  // component ilk kez render edildiğinde çalışacak

  return (
    <div className="bike-list">
      {bikes.length > 0 ? (
        bikes.map((bike) => <BikeCard key={bike.ref} bike={bike} />)  // bikes dizisindeki her eleman için BikeCard bileşenini render et
      ) : (
        <p>No bikes available.</p>  // Eğer bisiklet yoksa mesaj göster
      )}
    </div>
  );
};

export default BikeList;
