// src/components/BikeList.js
import React, { useEffect, useState } from 'react';
import { getBikes, addBike } from '../services/api';
import BikeCard from './BikeCard';

const BikeList = () => {
  const [bikes, setBikes] = useState([]);

  useEffect(() => {
    const fetchBikes = async () => {
      const fetchedBikes = await getBikes();
      setBikes(fetchedBikes);
    };
    fetchBikes();
  }, []);

  // Bisiklet ekleme fonksiyonu
  const handleAddBike = async (newBike) => {
    const addedBike = await addBike(newBike);
    setBikes((prevBikes) => [...prevBikes, addedBike]);  // Yeni bisikleti mevcut listeye ekliyoruz
  };

  return (
    <div className="bike-list">
      {bikes.length > 0 ? (
        bikes.map((bike) => (
          <BikeCard key={bike.id} bike={bike} />
        ))
      ) : (
        <p>No bikes available.</p>
      )}
      <button onClick={() => handleAddBike({ model: 'New Bike', manufacturer: 'Test', price: 1000, imageUrl: 'path_to_image' })}>
        Add New Bike
      </button>
    </div>
  );
};

export default BikeList;
