// src/components/AddBike.js
import React, { useState } from 'react';
import { addBike } from '../services/BikeService';

const AddBike = ({ onAddBike }) => {
  const [manufacturer, setManufacturer] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (manufacturer && price) {
      const newBike = { manufacturer, price: parseFloat(price) };
      await addBike(newBike);  // Backend'e veri gönder
      onAddBike(newBike);  // Frontend'de listeyi güncelle
      setManufacturer('');
      setPrice('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Manufacturer"
        value={manufacturer}
        onChange={(e) => setManufacturer(e.target.value)}
      />
      <input
        type="number"
        placeholder="Price"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      />
      <button type="submit">Add Bike</button>
    </form>
  );
};

export default AddBike;
