// src/components/BikeList.js
import React, { useEffect, useState } from 'react';
import { getBikes } from '../services/BikeService';
import BikeCard from './BikeCard';
import Dropdown from './Dropdown';

const BikeList = () => {
  const [bikes, setBikes] = useState([]);
  const [sortedBikes, setSortedBikes] = useState([]);

  useEffect(() => {
    const fetchBikes = async () => {
      const data = await getBikes();
      setBikes(data);
      setSortedBikes(data); // Başlangıçta sıralı veri, direkt olarak veriyi ayarlıyoruz
    };

    fetchBikes();
  }, []);

  const handleSortChange = (criteria) => {
    const sorted = [...bikes].sort((a, b) => {
      if (criteria === 'manufacturer') {
        return a.manufacturer.localeCompare(b.manufacturer);
      }
      return a.price - b.price;
    });
    setSortedBikes(sorted);
  };

  return (
    <div className="bike-list">
      <Dropdown onSortChange={handleSortChange} />
      {sortedBikes.length > 0 ? (
        sortedBikes.map((bike) => <BikeCard key={bike.id} bike={bike} />)
      ) : (
        <p>No bikes available.</p>
      )}
    </div>
  );
};

export default BikeList;
