import React, { useState } from 'react';
import { addBike } from '../services/api';

const NewBikeForm = ({ onBikeAdded }) => {
  const [newBike, setNewBike] = useState({ manufacturer: '', model: '', price: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewBike((prevBike) => ({
      ...prevBike,
      [name]: value,
    }));
  };

  const handleAddBike = async (e) => {
    e.preventDefault();
    if (newBike.manufacturer && newBike.model && newBike.price) {
      const addedBike = await addBike(newBike);
      onBikeAdded(addedBike); // Listeyi güncellemek için bir callback çağır
      setNewBike({ manufacturer: '', model: '', price: '' });
    } else {
      alert('All fields are required.');
    }
  };

  return (
    <form onSubmit={handleAddBike} className="add-bike-form">
      <h3>Add a New Bike</h3>
      <input
        type="text"
        name="manufacturer"
        placeholder="Manufacturer"
        value={newBike.manufacturer}
        onChange={handleInputChange}
        required
      />
      <input
        type="text"
        name="model"
        placeholder="Model"
        value={newBike.model}
        onChange={handleInputChange}
        required
      />
      <input
        type="number"
        name="price"
        placeholder="Price"
        value={newBike.price}
        onChange={handleInputChange}
        required
      />
      <button type="submit">Add Bike</button>
    </form>
  );
};

export default NewBikeForm;
