import React, { useState } from 'react';

const BikeList = ({ bikes }) => {
  const [favorites, setFavorites] = useState([]);

  const handleAddToFavorites = (bike) => {
    if (!favorites.some((fav) => fav.id === bike.id)) {
      setFavorites([...favorites, bike]);
    } else {
      alert('This bike is already in your favorites!');
    }
  };

  return (
    <div>
      <h2>Bike List</h2>
      <ul>
        {bikes.map((bike) => (
          <li key={bike.id}>
            {bike.manufacturer} - {bike.model}
            <button onClick={() => handleAddToFavorites(bike)}>Add to Favorites</button>
          </li>
        ))}
      </ul>

      <h2>My Favorites</h2>
      <ul>
        {favorites.map((fav) => (
          <li key={fav.id}>
            {fav.manufacturer} - {fav.model}
          </li>
        ))}
      </ul>
    </div>
  );
};
