// src/services/BikeService.js

let bikesData = [
    { id: 1, manufacturer: 'Trek', model: 'X1', price: 1200 },
    { id: 2, manufacturer: 'Specialized', model: 'Enduro', price: 2500 },
    { id: 3, manufacturer: 'Giant', model: 'Defy', price: 800 },
  ];
  
  export const getBikes = async () => {
    // Simulating a fetch request
    return new Promise((resolve) => {
      setTimeout(() => resolve(bikesData), 1000);
    });
  };
  
  export const addBike = async (newBike) => {
    newBike.id = Date.now(); // Assign unique ID based on timestamp
    bikesData.push(newBike);
    return newBike;
  };
