// src/components/AddBike.js
import React, { useState } from 'react';
import '../styles/addBike.css';

const AddBike = ({ onAddBike }) => {
  const [bike, setBike] = useState({ manufacturer: '', model: '', price: '' });
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBike((prevBike) => ({
      ...prevBike,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Form doğrulama
    if (!bike.manufacturer || !bike.model || !bike.price) {
      setError('All fields are required.');
      return;
    }

    if (isNaN(bike.price) || Number(bike.price) <= 0) {
      setError('Price must be a positive number.');
      return;
    }

    onAddBike({ ...bike, id: Date.now() });
    setBike({ manufacturer: '', model: '', price: '' });
    setError('');
  };

  return (
    <form className="add-bike-form" onSubmit={handleSubmit}>
      <h3>Add a New Bike</h3>
      {error && <p className="error-message">{error}</p>}
      <input
        type="text"
        name="manufacturer"
        placeholder="Manufacturer"
        value={bike.manufacturer}
        onChange={handleInputChange}
        required
      />
      <input
        type="text"
        name="model"
        placeholder="Model"
        value={bike.model}
        onChange={handleInputChange}
        required
      />
      <input
        type="number"
        name="price"
        placeholder="Price"
        value={bike.price}
        onChange={handleInputChange}
        required
      />
      <button type="submit">Add Bike</button>
    </form>
  );
};

export default AddBike;
