// src/components/AddBike.js
import React, { useState } from 'react';
import { addBike } from '../services/BikeService';
import './bikeForm.css';

const AddBike = ({ onAddBike }) => {
  const [model, setModel] = useState('');
  const [manufacturer, setManufacturer] = useState('');
  const [price, setPrice] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newBike = { model, manufacturer, price: parseInt(price), imageUrl };
    await addBike(newBike);
    onAddBike(newBike); // Adding bike to the list after submission
    alert('Bike added successfully!');
  };

  return (
    <div className="add-bike-form">
      <h2>Add a New Bike</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={model}
          onChange={(e) => setModel(e.target.value)}
          placeholder="Bike Model"
          required
        />
        <input
          type="text"
          value={manufacturer}
          onChange={(e) => setManufacturer(e.target.value)}
          placeholder="Manufacturer"
          required
        />
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          placeholder="Price"
          required
        />
        <input
          type="text"
          value={imageUrl}
          onChange={(e) => setImageUrl(e.target.value)}
          placeholder="Image URL"
          required
        />
        <button type="submit">Add Bike</button>
      </form>
    </div>
  );
};

export default AddBike;
