import React, { useState } from 'react';
import { addBike } from '../services/BikeService';

const AddBike = ({ onAddBike }) => {
  const [manufacturer, setManufacturer] = useState('');
  const [model, setModel] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (manufacturer && model && price) {
      const newBike = { manufacturer, model, price: parseFloat(price) };
      const result = await addBike(newBike);  // Backend'e veri gönder
      onAddBike(result);  // Frontend'de listeyi güncelle
      setManufacturer('');
      setModel('');
      setPrice('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Manufacturer"
        value={manufacturer}
        onChange={(e) => setManufacturer(e.target.value)}
      />
      <input
        type="text"
        placeholder="Model"
        value={model}
        onChange={(e) => setModel(e.target.value)}
      />
      <input
        type="number"
        placeholder="Price"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      />
      <button type="submit">Add Bike</button>
    </form>
  );
};

export default AddBike;
