import React, { useState, useEffect } from 'react';
import BikeList from './components/BikeList';
import AddBike from './components/AddBike';
import Dropdown from './components/Dropdown';
import { getBikes } from './services/BikeService';
import './styles/app.css'; // Yeni profesyonel tasar覺m

const App = () => {
  const [bikes, setBikes] = useState([]);
  const [sortBy, setSortBy] = useState('manufacturer');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBikes = async () => {
      try {
        const bikeData = await getBikes();
        setBikes(bikeData);
      } catch (err) {
        setError('Failed to fetch bikes. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    fetchBikes();
  }, []);

  const handleSortChange = (value) => {
    setSortBy(value);
  };

  const handleAddBike = (newBike) => {
    setBikes((prevBikes) => [...prevBikes, newBike]);
  };

  const sortedBikes = [...bikes].sort((a, b) => {
    if (sortBy === 'price') {
      return a.price - b.price;
    } else if (sortBy === 'manufacturer') {
      return a.manufacturer.localeCompare(b.manufacturer);
    }
    return 0;
  });

  return (
    <div className="app-container">
      <header className="header">
        <h1 className="app-title">Bike Shop</h1>
        <Dropdown onSortChange={handleSortChange} />
      </header>

      {loading && <div className="loading">Loading bikes...</div>}
      {error && <div className="error">{error}</div>}

      {!loading && !error && (
        <>
          <div className="product-container">
            <BikeList bikes={sortedBikes} />
          </div>

          {/* Alt k覺s覺mda eklenen bisikletler */}
          <div className="bike-list-container">
            {sortedBikes.map((bike) => (
              <div key={bike.id} className="bike-item">
                <img src={bike.image} alt={bike.model} className="bike-image" />
                <div className="bike-info">
                  <h3 className="bike-manufacturer">{bike.manufacturer}</h3>
                  <p className="bike-model">{bike.model}</p>
                  <p className="bike-price">${bike.price}</p>
                </div>
                <button className="add-to-cart-btn">Add to Cart</button>
              </div>
            ))}
          </div>

          <AddBike onAddBike={handleAddBike} />
        </>
      )}

      <footer>
        <p>&copy; 2024 BikeShop | All Rights Reserved</p>
      </footer>
    </div>
  );
};

export default App;
