// src/components/BikeList.js
import React, { useState, useEffect } from 'react';
import '../styles/bikeList.css';

const BikeList = ({ bikes, setBikes }) => {
  const [sortedBikes, setSortedBikes] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [sortBy, setSortBy] = useState('manufacturer');
  
  // New bike form state
  const [newBike, setNewBike] = useState({
    manufacturer: '',
    model: '',
    price: ''
  });

  useEffect(() => {
    setSortedBikes([...bikes].sort((a, b) => a.manufacturer.localeCompare(b.manufacturer)));
  }, [bikes]);

  const handleSortChange = (e) => {
    const sortOption = e.target.value;
    setSortBy(sortOption);
    let sorted = [...bikes];

    if (sortOption === 'manufacturer') {
      sorted = sorted.sort((a, b) => a.manufacturer.localeCompare(b.manufacturer));
    } else if (sortOption === 'lowestPrice') {
      sorted = sorted.sort((a, b) => a.price - b.price);
    } else if (sortOption === 'highestPrice') {
      sorted = sorted.sort((a, b) => b.price - a.price);
    }

    setSortedBikes(sorted);
  };

  const handleAddToFavorites = (bike) => {
    if (!favorites.some((fav) => fav.id === bike.id)) {
      setFavorites([...favorites, bike]);
    } else {
      alert('This bike is already in your favorites!');
    }
  };

  const handleNewBikeChange = (e) => {
    const { name, value } = e.target;
    setNewBike((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddNewBike = (e) => {
    e.preventDefault();

    // Add validation to check if bike data is complete
    if (newBike.manufacturer && newBike.model && newBike.price) {
      const newBikeData = {
        ...newBike,
        id: Date.now(), // Generate a unique ID for the new bike
      };

      // Update the bikes list
      setBikes([...bikes, newBikeData]);

      // Reset the new bike form
      setNewBike({
        manufacturer: '',
        model: '',
        price: ''
      });
    } else {
      alert('Please fill out all fields!');
    }
  };

  return (
    <div className="bike-list-container">
      {/* Sorting Section */}
      <div className="sort-section">
        <label htmlFor="sort" className="sort-label">Sort by:</label>
        <select id="sort" value={sortBy} onChange={handleSortChange} className="sort-dropdown">
          <option value="manufacturer">Manufacturer</option>
          <option value="lowestPrice">Lowest Price</option>
          <option value="highestPrice">Highest Price</option>
        </select>
      </div>

      {/* Bike List Section */}
      <div className="bike-list">
        <ul className="bike-list-items">
          {sortedBikes.map((bike) => (
            <li key={bike.id} className="bike-item">
              <div className="bike-details">
                <p className="bike-manufacturer">{bike.manufacturer}</p>
                <p className="bike-model">{bike.model}</p>
                <p className="bike-price">${bike.price}</p>
              </div>
              <button 
                className="favorite-button" 
                onClick={() => handleAddToFavorites(bike)}>
                Add to Favorites
              </button>
            </li>
          ))}
        </ul>
      </div>

      {/* Add New Bike Form */}
      <div className="add-bike-form">
        <h2>Add a New Bike</h2>
        <form onSubmit={handleAddNewBike} className="bike-form">
          <input
            type="text"
            name="manufacturer"
            value={newBike.manufacturer}
            onChange={handleNewBikeChange}
            placeholder="Manufacturer"
            className="input-field"
          />
          <input
            type="text"
            name="model"
            value={newBike.model}
            onChange={handleNewBikeChange}
            placeholder="Model"
            className="input-field"
          />
          <input
            type="number"
            name="price"
            value={newBike.price}
            onChange={handleNewBikeChange}
            placeholder="Price"
            className="input-field"
          />
          <button type="submit" className="add-bike-button">Add Bike</button>
        </form>
      </div>

      {/* Favorites Section */}
      <div className="favorites-section">
        <h2>My Favorites</h2>
        <ul className="favorites-list">
          {favorites.length > 0 ? (
            favorites.map((fav) => (
              <li key={fav.id} className="favorite-item">
                {fav.manufacturer} - {fav.model}
              </li>
            ))
          ) : (
            <p className="no-favorites">No favorites yet!</p>
          )}
        </ul>
      </div>
    </div>
  );
};

export default BikeList;
