// src/services/api.js

export const getBikes = async () => {
    try {
      const response = await fetch('/bike.json'); // Fetching data from the public folder
      if (!response.ok) {
        throw new Error('Failed to fetch bike data');
      }
      const bikesData = await response.json(); // Parsing JSON response
      return bikesData; // Returning parsed bike data
    } catch (error) {
      console.error('Error fetching bikes:', error);
      return [];
    }
  };
  
  export const addBike = async (newBike) => {
    // Add logic for adding a new bike if needed.
    // You can persist the new bike to localStorage or API here.
    return newBike;
  };
