// src/services/api.js
import bikesData from '../assets/data/bikes.json';  // Importing JSON directly

export const getBikes = async () => {
  return new Promise((resolve) => {
    setTimeout(() => resolve(bikesData), 1000);  // Returning the JSON data directly
  });
};

export const addBike = async (newBike) => {
  newBike.id = Date.now(); // Assign unique ID based on timestamp
  bikesData.push(newBike);  // Update the bikesData directly
  return newBike;
};
