export const getBikes = async (sortOption) => {
    const response = await fetch(`http://localhost:5000/api/bikes?sort=${sortOption}`);
    const bikes = await response.json();
    return bikes;
  };
  
  export const createBike = async (bikeData) => {
    const response = await fetch('http://localhost:5000/api/bikes', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(bikeData),
    });
    return await response.json();
  };
