import React, { useState, useEffect } from 'react';
import { getBikes, addToFavorites } from './bikeService'; // Import bikeService

const BikeList = () => {
  const [bikes, setBikes] = useState([]);
  const [sortBy, setSortBy] = useState('manufacturer');
  const [favorites, setFavorites] = useState(new Set());

  // Fetch bikes when component mounts or sort order changes
  useEffect(() => {
    getBikes(sortBy).then((response) => {
      setBikes(response.data);
    }).catch((error) => {
      console.error("Error fetching bikes", error);
    });
  }, [sortBy]);

  const handleSortChange = (event) => {
    setSortBy(event.target.value);
  };

  const handleAddToFavorites = (bikeId) => {
    if (!favorites.has(bikeId)) {
      addToFavorites(bikeId).then(() => {
        setFavorites(new Set(favorites.add(bikeId)));
      }).catch((error) => {
        console.error("Error adding to favorites", error);
      });
    } else {
      alert("Bike is already in favorites!");
    }
  };

  return (
    <div>
      <h2>Bike Catalogue</h2>
      
      <label htmlFor="sort">Sort by: </label>
      <select id="sort" value={sortBy} onChange={handleSortChange}>
        <option value="manufacturer">Manufacturer</option>
        <option value="lowest_price">Lowest Price</option>
        <option value="highest_price">Highest Price</option>
      </select>

      <ul>
        {bikes.length > 0 ? (
          bikes.map((bike) => (
            <li key={bike._id}>
              <div>
                <img src={`/assets/images/bikes/${bike.model}.png`} alt={bike.model} />
                <h3>{bike.manufacturer} - {bike.model}</h3>
                <p>Price: ${bike.price}</p>
                <button onClick={() => handleAddToFavorites(bike._id)}>
                  Add to Favorites
                </button>
              </div>
            </li>
          ))
        ) : (
          <p>Loading bikes...</p>
        )}
      </ul>
    </div>
  );
};

export default BikeList;
