import React, { useState } from 'react';

const AddBike = ({ onAddBike }) => {
  const [newBike, setNewBike] = useState({
    manufacturer: '',
    model: '',
    price: ''
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setNewBike({ ...newBike, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newBike.manufacturer || !newBike.model || !newBike.price) {
      setError('All fields must be filled!');
      return;
    }
    onAddBike({ ...newBike, id: Date.now() });
    setNewBike({ manufacturer: '', model: '', price: '' });
    setError('');
  };

  return (
    <div className="add-bike-form">
      <h2>Add a New Bike</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="manufacturer"
          value={newBike.manufacturer}
          onChange={handleChange}
          placeholder="Manufacturer"
          className="input-field"
        />
        <input
          type="text"
          name="model"
          value={newBike.model}
          onChange={handleChange}
          placeholder="Model"
          className="input-field"
        />
        <input
          type="number"
          name="price"
          value={newBike.price}
          onChange={handleChange}
          placeholder="Price"
          className="input-field"
        />
        {error && <p className="error-message">{error}</p>}
        <button type="submit" className="add-bike-button">Add Bike</button>
      </form>
    </div>
  );
};

export default AddBike;
