// src/components/BikeList.js
import React, { useState, useEffect } from 'react';
import BikeCard from './BikeCard';
import { getBikes } from '../services/BikeService';
import './bikeList.css';

const BikeList = ({ sortBy }) => {
  const [bikes, setBikes] = useState([]);

  useEffect(() => {
    const fetchBikes = async () => {
      const bikeData = await getBikes();
      if (sortBy === 'price') {
        setBikes(bikeData.sort((a, b) => a.price - b.price));
      } else if (sortBy === 'manufacturer') {
        setBikes(bikeData.sort((a, b) => a.manufacturer.localeCompare(b.manufacturer)));
      }
    };

    fetchBikes();
  }, [sortBy]);

  return (
    <div className="card-grid">
      {bikes.map((bike) => (
        <BikeCard key={bike.id} bike={bike} />
      ))}
    </div>
  );
};

export default BikeList;
