// src/AddBike.js
import React, { useState } from 'react';
import { addBike } from './BikeService'; // API service for adding bikes

const AddBike = ({ onBikeAdded }) => {
  const [manufacturer, setManufacturer] = useState('');
  const [model, setModel] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newBike = { manufacturer, model, price: parseFloat(price) };
    try {
      const addedBike = await addBike(newBike);
      onBikeAdded(addedBike); // Parent list will update
      setManufacturer('');
      setModel('');
      setPrice('');
    } catch (error) {
      console.error('Error adding bike:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add New Bike</h2>
      <label>
        Manufacturer:
        <input
          type="text"
          value={manufacturer}
          onChange={(e) => setManufacturer(e.target.value)}
          required
        />
      </label>
      <label>
        Model:
        <input
          type="text"
          value={model}
          onChange={(e) => setModel(e.target.value)}
          required
        />
      </label>
      <label>
        Price:
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />
      </label>
      <button type="submit">Add Bike</button>
    </form>
  );
};

export default AddBike;
