// AddBike.js
import React, { useState } from 'react';
import '../styles/addBike.css';


const AddBike = ({ onAddBike }) => {
  const [bike, setBike] = useState({ manufacturer: '', model: '', price: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBike((prevBike) => ({
      ...prevBike,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (bike.manufacturer && bike.model && bike.price) {
      onAddBike({ ...bike, id: Date.now() });
      setBike({ manufacturer: '', model: '', price: '' });
    } else {
      alert('All fields are required.');
    }
  };

  return (
    <form className="add-bike-form" onSubmit={handleSubmit}>
      <h3>Add a New Bike</h3>
      <input
        type="text"
        name="manufacturer"
        placeholder="Manufacturer"
        value={bike.manufacturer}
        onChange={handleInputChange}
        required
      />
      <input
        type="text"
        name="model"
        placeholder="Model"
        value={bike.model}
        onChange={handleInputChange}
        required
      />
      <input
        type="number"
        name="price"
        placeholder="Price"
        value={bike.price}
        onChange={handleInputChange}
        required
      />
      <button type="submit">Add Bike</button>
    </form>
  );
};

export default AddBike;
