import axios from 'axios';

const API_URL = 'http://localhost:5000'; // Flask backend URL

// Function to get bikes with sorting option
export const getBikes = (sortBy = 'manufacturer') => {
  return axios.get(`${API_URL}/bikes`, {
    params: { sort_by: sortBy },
  });
};

// Function to add a new bike
export const addBike = (bike) => {
  return axios.post(`${API_URL}/bikes`, bike);
};

// Function to add a bike to favorites
export const addToFavorites = (bikeId) => {
  return axios.post(`${API_URL}/favourites`, { bike_id: bikeId });
};
