import React, { useState, useEffect } from 'react';

const BikeList = ({ bikes }) => {
  const [sortedBikes, setSortedBikes] = useState([]);

  useEffect(() => {
    // Varsayılan sıralama: Üretici adına göre
    setSortedBikes([...bikes].sort((a, b) => a.manufacturer.localeCompare(b.manufacturer)));
  }, [bikes]);

  const handleSortChange = (e) => {
    const sortBy = e.target.value;
    let sorted = [];
    if (sortBy === 'manufacturer') {
      sorted = [...bikes].sort((a, b) => a.manufacturer.localeCompare(b.manufacturer));
    } else if (sortBy === 'lowestPrice') {
      sorted = [...bikes].sort((a, b) => a.price - b.price);
    } else if (sortBy === 'highestPrice') {
      sorted = [...bikes].sort((a, b) => b.price - a.price);
    }
    setSortedBikes(sorted);
  };

  return (
    <div>
      <label htmlFor="sort">Sort by:</label>
      <select id="sort" onChange={handleSortChange}>
        <option value="manufacturer">Manufacturer Name</option>
        <option value="lowestPrice">Lowest Price</option>
        <option value="highestPrice">Highest Price</option>
      </select>

      <ul>
        {sortedBikes.map((bike) => (
          <li key={bike.id}>
            {bike.manufacturer} - {bike.model} - ${bike.price}
          </li>
        ))}
      </ul>
    </div>
  );
};
